$(()=>{let t=function(t){var a=t.split("?")[1];if(!a)return!1;var s=a.split("&");if(!s)return!1;for(var c={},r=0;r<s.length;r++){var l=s[r].split("=");c[l[0]]=l[1]}return c}(window.location.href);$.ajax({url:"http://127.0.0.1:3000/api/getproduct",data:{productid:t.productId},dataType:"json",success:t=>{let a=t.result;$(".product-name").text(a.productName),$(".product-img img").attr("src",a.productImg.src),$(".red").text($.trim(a.bjShop))}}),$.ajax({url:"http://127.0.0.1:3000/api/getproductcom",data:{productid:t.productId},dataType:"json",success:t=>{let a=t.result,s="";a.forEach(t=>{s+=`\n                <li>\n                    <div class="clearfix"><span class="pull-left com-name">${t.comName}</span><span\n                            class="pull-right com-time">${t.comTime}</span></div>\n                    <div class="clearfix"><span class="star pull-left com-star"><em class="star5"></em></span><span\n                            class="pull-right com-from">${t.comFrom}</span></div>\n                    <div class="com-content">\n                        ${t.comContent}\n                    </div>\n                </li>\n                `}),$(".product-com-list ul").html(s)}})});