$(() => {
  $.ajax({
    url: 'http://127.0.0.1:3000/api/getindexmenu',
    type: 'get',
    dataType: 'json',
    success: e => {
      let { result: t } = e,
        n = ''
      t.forEach((e, t) => {
        n += `\n                <div class="menu-item ${
          t >= 8 ? 'active' : ''
        }">\n                <a href="${
          e.titlehref
        }.html">\n                <img src="./img/${
          e.img
        }">\n                <p>${
          e.name
        }</p>\n                </a>\n                </div>\n                `
      }),
        $('#menu .row').html(n)
    }
  }),
    $('.row').on('click', '.menu-item', function() {
      $(this)
        .nextAll()
        .toggleClass('active')
    }),
    Menus(1)
})
