$(() => {
  window.Menus = (n, c) => {
    $.ajax({
      url: 'http://127.0.0.1:3000/api/getmoneyctrl',
      data: { pageid: n },
      dataType: 'json',
      success: n => {
        let { result: a } = n,
          s = ''
        a.forEach(n => {
          s += `\n                    <li class="clearfix ui-border-b">\n                    <a href="moneyproduct.html?productid=${
            n.productId
          }">\n                        <div class="pic">\n                        <img src="${
            n.productImgLg.src
          }" alt="${
            n.productName
          }">\n                        </div>\n                        <div class="clearfix info">\n                            <div class="title">\n                            ${
            n.productName
          }\n                            <span>${
            n.productPinkage
          }å…ƒ</span>\n                            </div>\n                            <div class="other">\n                            <span class="mall">\n                            ${
            n.productFrom
          } | ${
            n.productTime
          }\n                            </span>\n                            <span class="comment">\n                            <i class="icon-comment"></i>\n                            ${
            n.productComCount
          }</span>\n                            </div>\n                        </div>\n                    </a>\n                </li>\n                    `
        }),
          $('#recommen-product ul').html(s),
          c && c(n.count)
      }
    })
  }
})
